async fn debug_state(State(state): State<AppState>) -> impl IntoResponse {
    let sessions = state.session_manager.get_stats();
    let connections = state.connections.read().await.len();
    let overlays = state.overlay_store.get_stats();
    let slides = state.slide_service.get_stats();

    Json(DebugStateResponse {
        sessions: SessionStats {
            active: sessions.active_count,
            total_participants: sessions.total_participants,
            oldest_session: sessions.oldest_created_at,
        },
        connections: ConnectionStats {
            websocket_count: connections,
            pending_messages: state.pending_message_count(),
        },
        overlays: OverlayStats {
            cached: overlays.cache_entries,
            cache_size_mb: overlays.cache_size_bytes / 1_000_000,
        },
        slides: SlideStats {
            available: slides.available_count,
            cached_handles: slides.cached_handle_count,
        },
        uptime_seconds: state.start_time.elapsed().as_secs(),
        version: env!("CARGO_PKG_VERSION").to_string(),
    })
}
