```rust
use jsonschema::JSONSchema;

pub fn validate_overlay_manifest(manifest: &Value) -> Result<(), ValidationError> {
    let schema = include_str!("../schemas/overlay-manifest.json");
    let schema: Value = serde_json::from_str(schema)?;
    let compiled = JSONSchema::compile(&schema)?;

    let result = compiled.validate(manifest);
    if let Err(errors) = result {
        let messages: Vec<_> = errors.map(|e| e.to_string()).collect();
        return Err(ValidationError::Schema(messages));
    }

    Ok(())
}
```

