function Sidebar({ isOpen, onToggle }: Props) {
    useEffect(() => {
        const handleKeydown = (e: KeyboardEvent) => {
            if ((e.metaKey || e.ctrlKey) && e.key === '\\') {
                e.preventDefault();
                onToggle();
            }
        };
        window.addEventListener('keydown', handleKeydown);
        return () => window.removeEventListener('keydown', handleKeydown);
    }, [onToggle]);

    return (
        <aside
            className={`
                transition-[width] duration-250 ease-out
                ${isOpen ? 'w-[280px]' : 'w-0'}
                overflow-hidden
            `}
            aria-hidden={!isOpen}
        >
            <nav className="w-[280px] h-full overflow-y-auto">
                {/* Sections */}
            </nav>
        </aside>
    );
}

function SidebarSection({ title, children, defaultCollapsed }: Props) {
    const [isExpanded, setIsExpanded] = useState(!defaultCollapsed);

    return (
        <div>
            <button
                onClick={() => setIsExpanded(!isExpanded)}
                aria-expanded={isExpanded}
                className="w-full flex items-center justify-between p-3 hover:bg-gray-50"
            >
                <span>{title}</span>
                <ChevronIcon className={`transition-transform ${isExpanded ? 'rotate-90' : ''}`} />
            </button>
            <div
                className={`overflow-hidden transition-[max-height] ${isExpanded ? 'max-h-[500px]' : 'max-h-0'}`}
            >
                {children}
            </div>
        </div>
    );
}
```

